SELECT DISTINCT * FROM (
SELECT target_id as targetId, target_type as targetType FROM ( (SELECT DISTINCT a.priority,a.target_type, c.target_id from content_fragment c LEFT JOIN attribute a on c.attribute_id = a.id LEFT JOIN dictionary d on a.data_type = d.type and d.code = c.content LEFT JOIN ich_project ip on a.target_type = 0 AND ip.id = c.target_id LEFT JOIN ich_category ic on ic.id = ip.ich_category_id where (d.type != 101)
AND d.name LIKE CONCAT('%',#{keyword,jdbcType=VARCHAR},'%')
AND a.target_type = #{type,jdbcType=INTEGER}
AND ic.gb_category LIKE CONCAT(#{gb_category_code,jdbcType=VARCHAR},'%')
) UNION (SELECT DISTINCT CASE WHEN c.content = #{keyword,jdbcType=VARCHAR} THEN a.priority - 0.5 ELSE a.priority END priority, a.target_type as targetType, c.target_id as targetId from content_fragment c LEFT JOIN attribute a on c.attribute_id = a.id LEFT JOIN ich_project ip on a.target_type = 0 AND ip.id = c.target_id LEFT JOIN ich_category ic on ic.id = ip.ich_category_id where 1=1
AND c.content LIKE CONCAT('%',#{keyword,jdbcType=VARCHAR},'%')
AND a.target_type = #{type,jdbcType=INTEGER}
AND ic.gb_category LIKE CONCAT(#{gb_category_code,jdbcType=VARCHAR},'%')
) ) u ORDER BY priority ) u2 LIMIT #{pageBegin,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER};

SELECT count(*) FROM ( SELECT DISTINCT target_id, target_type FROM ( (SELECT a.target_type, c.target_id from content_fragment c LEFT JOIN attribute a on c.attribute_id = a.id LEFT JOIN dictionary d on a.data_type = d.type and d.code = c.content LEFT JOIN ich_project ip on a.target_type = 0 AND ip.id = c.target_id LEFT JOIN ich_category ic on ic.id = ip.ich_category_id where 1=1 AND (d.type != 101)
AND d.name LIKE CONCAT('%',#{keyword,jdbcType=VARCHAR},'%')
AND a.target_type = #{type,jdbcType=INTEGER}
AND ic.gb_category LIKE CONCAT(#{gb_category_code,jdbcType=VARCHAR},'%')
) UNION (SELECT
a.target_type, c.target_id from content_fragment c LEFT JOIN attribute a on c.attribute_id = a.id LEFT JOIN ich_project ip on a.target_type = 0 AND ip.id = c.target_id LEFT JOIN ich_category ic on ic.id = ip.ich_category_id where 1=1
AND c.content LIKE CONCAT('%',#{keyword,jdbcType=VARCHAR},'%')
AND a.target_type = #{type,jdbcType=INTEGER}
AND ic.gb_category LIKE CONCAT(#{gb_category_code,jdbcType=VARCHAR},'%')
) ) u) records

